import React, { useEffect, useState } from "react";
import axios from 'axios';

const App = () => {

    const [state, setSate] = useState('')

    const headers = {
        "Content-Type": "application/json",
        "apikey": "94f45592c5ac44ca9a4438ed0f5d8442",
        "workspace": "541c7e31a746443a8da969be7f2d8ebf"
    };

    const shorten = url => {
        let endpoint = "https://api.rebrandly.com/v1/links";
        let linkRequest = {
            destination: url,
            domain: { fullName: "rebrand.ly" }
        }

    const apiCall = {
        method: 'post',
        url: endpoint,
        data: linkRequest,
        headers: headers
    }
    let apiResponse = axios(apiCall);
    let link = apiResponse.data;
    return link.shortUrl;
    }

    let shortUrl = shorten("https://www.youtube.com/channel/UCHK4HD0ltu1-I212icLPt3g");
    console.log(shortUrl);

    return (
        <div className="App">
            <header className="App-header">Hello</header>
            <div>
                <input></input>
                <button>Get your shorten URL</button>
                <p>${shortUrl}</p>
            </div>
        </div>
    );
}

export default App;
