import React, { useState } from "react";
import axios from 'axios';
import linkIcon from './link.png'

const App = () => {

    const [state, setSate] = useState('')
    const [shortenedUrl, setShortenedUrl] = useState('')
    const [isValid, setIsValid] = useState(false)

    const handleChange = (e) => {
        setSate(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const headers = {
            "Content-Type": "application/json",
            "apikey": process.env.REACT_APP_API_KEY,
            "workspace": process.env.REACT_APP_WORKSPACE
        };

        const data = {
            destination: state,
            domain: { fullName: "rebrand.ly" }
        }

        const endpoint = "https://api.rebrandly.com/v1/links";

        if (state !== '') {
            axios.post(endpoint, data, { headers: headers })
            .then((res) => setShortenedUrl(res.data.shortUrl))
            .then(() => setIsValid(true))
            .catch((err) => err)
        } else alert('You need to paste the link first');
    }

    const handleCopy = () => {
        navigator.clipboard.writeText(shortenedUrl)
        .then(() => alert('Successfully copied link to clipboard'))
        .catch(() => alert('Could not copy text'))
    }

    return (
        <div className="container">
            <header>URL Shortener</header>
            <div className="input-container">
                <h2 className="sub-title">Paste the URL to be shortened</h2>
                <div className="input-form">
                    <input onChange={e => handleChange(e)} placeholder='example.com'></input>
                    <button onClick={e => handleSubmit(e)}><img src={linkIcon} alt="example" />
                    Shorten it</button>
                </div>
            </div>
            {isValid && 
                <div className="result-container">
                    <h2 className="sub-title">Link Generated</h2>
                    <p>Your shortened URL is: {shortenedUrl}</p>
                    <button onClick={handleCopy}>Copy Link</button>
                </div>
            }
        </div>
    );
}

export default App;
