        shorten("https://www.youtube.com/channel/UCHK4HD0ltu1-I212icLPt3g");
    }

    return (
        <div className="App">
            <header className="App-header">Hello</header>
            <div>
                <input onChange={e => handleChange(e)}></input>
                <button onClick={e => handleSubmit(e)}>Get your shorten URL</button>
                {/* <p>${shortUrl}</p> */}
            </div>
        </div>
    );
}

export default App;
