import React, { useEffect, useState } from "react";
import axios from 'axios';

const App = () => {

    const [state, setSate] = useState('')

    const headers = {
        "Content-Type": "application/json",
        "apikey": process.env.REACT_APP_API_KEY,
        "workspace": process.env.REACT_APP_WORKSPACE
    };

    const data = {
        destination: "https://stackoverflow.com/questions/57847626/using-async-await-inside-a-react-functional-component",
        domain: { fullName: "rebrand.ly" }
    }

    const endpoint = "https://api.rebrandly.com/v1/links";

    const handleChange = (e) => {
        setSate(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(endpoint, {
            method: "POST",
            headers: headers,
            body: JSON.stringify(data)
        })
        .then(res => res.json())
        .then((actualData) => console.log(actualData))
        .catch((err) => err)
    }

    return (
        <div className="App">
            <header className="App-header">Hello</header>
            <div>
                <input required='yes' onChange={e => handleChange(e)}></input>
                <button onClick={e => handleSubmit(e)}>Get your shorten URL</button>
                {/* <p>${shortUrl}</p> */}
            </div>
        </div>
    );
}

export default App;
