import React, { useState } from "react";
import axios from 'axios';
import linkIcon from './link.png'
import logo from './logo.svg'

const App = () => {

    const [state, setSate] = useState('')
    const [shortenedUrl, setShortenedUrl] = useState('')
    const [isValid, setIsValid] = useState(false)
    const [tooltipText, setTooltipText] = useState('Click to Copy')

    const handleChange = (e) => {
        setSate(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const headers = {
            "Content-Type": "application/json",
            "apikey": process.env.REACT_APP_API_KEY,
            "workspace": process.env.REACT_APP_WORKSPACE
        };

        const data = {
            destination: state,
            domain: { fullName: "rebrand.ly" }
        }

        const endpoint = "https://api.rebrandly.com/v1/links";
        e.target.reset();
        setTooltipText('Click to Copy')
        if (state !== '') {
            axios.post(endpoint, data, { headers: headers })
            .then((res) => setShortenedUrl(res.data.shortUrl))
            .then(() => setIsValid(true))
            .catch((err) => err)
        } else alert('You need to paste the link first');
    }

    const handleCopy = () => {
        navigator.clipboard.writeText(shortenedUrl)
        .then(() => setTooltipText("Copied"))
        .catch(() => alert('Could not copy text'))
    }

    return (
        <div className="container">
            <header><img src={logo} className="logo" alt="logo" /> URL Shortener</header>
            <div className="input-container">
                <h2 className="sub-title">Paste the URL to be shortened</h2>
                <form className="input-form" onSubmit={e => handleSubmit(e)}>
                    <div className="url-input">
                        <label for="url">Select Domain</label>
                        <input name="url" autoFocus required type='url' onChange={e => handleChange(e)} placeholder='example.com/a-very-long-slug'>
                        </input>
                    </div>
                    <div className="select-domain">
                        <label for="Select Domain">Select Domain</label>
                        <select name="Select Domain">
                            <option value="rebrand.ly">rebrand.ly</option>
                            <option disabled value="custom">Custom domain (coming soon)</option>
                        </select>   
                    </div>

                    <button type="submit"><img className="button-icon" src={linkIcon} alt="link icon" />
                    Shorten it</button>
                </form>
            </div>
            {isValid && 
                <div className="result-container">
                    <p>Your shortened URL is:</p>
                    <button tooltip={tooltipText} tooltip-position="bottom" onClick={handleCopy}>{shortenedUrl}</button>
                </div>
            }
        </div>
    );
}

export default App;
