import React, { useEffect, useState } from "react";
import axios from 'axios';

const App = () => {

    const [state, setSate] = useState('')
    const [shortenedUrl, setShortenedUrl] = useState('')
    const [isValid, setIsValid] = useState(false)

    const handleChange = (e) => {
        setSate(e.target.value)
        console.log(state, isValid)
    }

    // let shortenedUrl = ''

    const handleSubmit = (e) => {
        e.preventDefault();
        const headers = {
            "Content-Type": "application/json",
            "apikey": process.env.REACT_APP_API_KEY,
            "workspace": process.env.REACT_APP_WORKSPACE
        };

        const data = {
            destination: state,
            domain: { fullName: "rebrand.ly" }
        }

        const endpoint = "https://api.rebrandly.com/v1/links";

        // axios.post(endpoint, data, { headers: headers })
        //     .then((res) => setShortenedUrl(res.data.shortUrl))
        //     .catch((err) => err)


        if (state !== '') {
            axios.post(endpoint, data, { headers: headers })
            .then((res) => setShortenedUrl(res.data.shortUrl))
            .then(() => setIsValid(true))
            .catch((err) => err)
        } else return null;
    }

    return (
        <div className="container">
            <header className="app-header">URL Shortener</header>
            <div>
                <input onChange={e => handleChange(e)} placeholder='Paste link'></input>
                <button onClick={e => handleSubmit(e)}>Get your shorten URL</button>
                {isValid && 
                <div>
                <p>Your shortened URL is: {shortenedUrl}</p>
                </div>
                }
            </div>
        </div>
    );
}

export default App;
